import React from 'react'
import { compose, lt, length, filter, propEq } from 'ramda'
import './style.css'
import './submit_button.css'
import './form.css'
var bcrypt = require('bcryptjs');
var hash
eslint-disable-next-line
const { redibase } = require('./connect')
const valid_users = [
    {
        username: 'sholom',
        password: 'jacks'
    },
    {
        username: 'mendel',
        password: 'hacks'
    }
]

export class App extends React.Component {
    state = {
        username: '',
        password: '',
        router: false
    }

    login = (username, password) => {
        if (length(username) === 0 || length(password) === 0) return
        console.log("hi")
        if (this.is_valid(username, password)) {
            this.setState({ router: true })
            hash = bcrypt.hashSync("bcrypt-connection-secured");
            localStorage.setItem("hash", hash)
            window.location = '/top'
        } else {
            alert("The login is incorrect, you may not enter the land of padah")
        }
    }

    is_valid = (username, password) => compose(
        lt(0),
        length,
        filter(propEq('password', password)),
        filter(propEq('username', username))
    )(valid_users)

    render() {

        let hasht = localStorage.getItem("hash")

        if (hasht !== null) {
                window.location = '/users'
        }

        return (
            <div>
                <center>
                    <section className="overlay">
                        <h1 id="first">Sign In</h1>
                        <label>
                            <input type="text" required onChange={(e) => { this.setState({ username: e.target.value }) }} />
                            <div class="label-text">Username</div>
                        </label>
                        <label>
                            <input type="text" type="password" required onChange={(e) => { this.setState({ password: e.target.value }) }} />
                            <div class="label-text">Password</div>
                        </label>
                        <button onClick={() => this.login(this.state.username, this.state.password)}>Submit</button>
                        {/* <a href="#" class="animated-button1" onClick={go}>
              <span></span>
              <span></span>
              <span></span>
              <span></span>
              Submit
                </a> */}
                    </section>
                </center>
            </div>
        )
    }
}

export default App;
