import React from 'react'
import { compose, lt, length, filter, propEq } from 'ramda'
import './style.css'
import './submit_button.css'
import './form.css'
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'
import Users from './users'
import Contact from './contact'
const { redibase } =require('./connect')

export class App extends React.Component {
    state = {
        username: '',
        password: '',
        router: false
    }

    login = (username, password) => {
        if (length(username) === 0 || length(password) === 0) return

        const valid_users = [
            {
                username: 'sholom',
                password: 'jacks'
            },
            {
                username: 'mendel',
                password: 'hacks'
            }
        ]

        if (this.is_valid(username, password)) {
            // alert(`Welcome ${username}`)

            window.location = '/users'
            redibase.set("router", "true")
        } else {
            alert("The login is incorrect, you may not enter the land of padah")
        }
    }

    is_valid = (username, password) => compose(
        lt(0),
        length,
        filter(propEq('password', password)),
        filter(propEq('username', username))
    )(this.valid_users)


    render() {
        // let router = redibase.get("router")
        // if (router === "true") {
        //     return <Router>
        //         <Route exact path="/" component={App} />
        //         <Route path="/users" component={Users} />
        //         <Route path="/contact" component={Contact} />
        //     </Router>
        //     redibase.delete("router")
        // }
        return (
            <center>
                <section className="overlay">
                    <form>
                        <h1 id="first">Sign In</h1>
                        <label>
                            <input type="text" required onChange={(e) => { this.setState({ username: e.target.value }) }} />
                            <div class="label-text">Username</div>
                        </label>
                        <label>
                            <input type="text" required type="password" onChange={(e) => { this.setState({ password: e.target.value }) }} />
                            <div class="label-text">Password</div>
                        </label>
                        <button onClick={() => this.login(this.state.username, this.state.password)}>Enter the secret world of padah</button>
                        {/* <a href="#" class="animated-button1" onClick={go}>
              <span></span>
              <span></span>
              <span></span>
              <span></span>
              Enter the secret world of padah
                </a> */}
                    </form>
                    {/* <h2>If you want to sign up you cant. haha.</h2> */}

                    {/* <form action="submission.html" method="GET">
            <label for="usernames" id="username">Username:</label>
            <br />
            <input id="usernames" name="username" type="text" minlength="3" maxlength="15" />
            <br />
            <label for="usernames" id="username">Password:</label>
            <br />
            <input id="usernames" name="pw" type="password" required minlength="8" maxlength="30" />
            <br /> <br />

            <a href="#" class="animated-button1" onClick={go}>
              <span></span>
              <span></span>
              <span></span>
              <span></span>
              Enter the secret world of padah
                </a>
          </form> */}

                </section>
            </center>
        )
    }
}
export default App;
