import React from 'react'
var bcrypt = require('bcryptjs')

function go() {
    
    let hash = localStorage.getItem("hash")

    let decide = bcrypt.compare("bcrypt-connection-secured", hash)

    if (decide === true) {
        window.location = '/cmds'
    }
}

function hi(e) {
    if (e.target.value === 'Addaccount') {
        alert("goog")
    }
}


class Admin extends React.Component {
    state = {
        cmd: '',
        go: false
    }
    runcmd = (e) => {
        console.log(e)
        if (e === 'Addaccount') {
            alert("goog")
            this.setState({cmd: ''})
        }
    }
    render() {


        if (this.state.go === true) {
            hi(this.state.cmd)
        }
        return (
            <div>
                <label>
                    <input type="text" required value={this.state.cmd} onChange={(e) => { this.setState({ cmd: e.target.value }); this.state.cmd.toUpperCase() }} />
                    <div class="label-text">Command</div>
                </label>

                <br />
                <br />
                <button type="submit" onClick={() => this.runcmd(this.state.cmd)} >Run</button>
                <button onClick={go}>Commands</button>
            </div>
        )
    }
}

export default Admin
