import { length, lt, filter, propEq, compose } from 'ramda'
import React from 'react'
import './form.css'
import './style.css'
import './submit_button.css'
var bcrypt = require('bcryptjs');
var hash
function go() {

    let hash = localStorage.getItem("hash")

    let decide = bcrypt.compare("bcrypt-connection-secured", hash)

    if (decide === true) {
        window.location = '/cmds'
    }
}


export const valid_users = [
    {
        username: 'sholom',
        password: 'jacks'
    },
    {
        username: 'mastermind',
        password: 'emmer'
    }, {
        username: 'mendel',
        password: 'jacks'
    }
]

export class App extends React.Component {
    state = {
        username: '',
        password: '',
        router: false,
        view: 'password',
        cmd: '',
        go: false,
        track: false,
        usernamet: ''
    }


    componentDidMount() {
        let hasht = localStorage.getItem("hash") || ''
        let decide = bcrypt.compareSync("bcrypt-connection-secured", hasht)
        if (decide === true) {
            window.location = '/intro'
        } else {
            // do nothing
        }
    }

    show = () => {
        if (this.state.view === 'password') {
            this.setState({ view: 'text' })
        } else {
            this.setState({ view: 'password' })
        }
    }

    login = (username, password) => {
        if (length(username) === 0 || length(password) === 0) return
        if (this.is_valid(username, password)) {
            this.setState({ router: true })
            hash = bcrypt.hashSync("bcrypt-connection-secured");
            localStorage.setItem("hash", hash)
            window.location = '/intro'
        } else {
            alert("The login is incorrect.")
        }
    }

    is_valid = (username, password) => compose(
        lt(0),
        length,
        filter(propEq('password', password)),
        filter(propEq('username', username))
    )(valid_users)

    runcmd = (e) => {
        if (e === 'Track') {
            this.setState({ track: true })
        }
    }

    gousername = (e) => {
        let hasht = localStorage.getItem("hash")
        let decide = bcrypt.compareSync("bcrypt-connection-secured", hasht)
        if (decide === true) {
            window.location = '/admin/usernames'
        } else {
            // do nothing
        }
    }

    render() {
        if (window.location === '/') {
            // Sign in page
            return (
                <div>

                    <center>
                        <section className="overlay">
                            <h1 id="first">Sign In</h1>
                            <label>
                                <input type="text" required onChange={(e) => { this.setState({ username: e.target.value }) }} />
                                <div class="label-text">Username</div>
                            </label>
                            <label>
                                <input type={this.state.view} required onChange={(e) => { this.setState({ password: e.target.value }) }} />
                                <div class="label-text">Password</div>
                            </label>
                            <img id="fontlc" src="https://cdn2.iconfinder.com/data/icons/flaticons-solid/18/eye-1-512.png" alt="Show Password" width="50" onClick={this.show} />
                            <br />
                            <button onClick={() => this.login(this.state.username, this.state.password)}>Submit</button>
                            {/* <a href="#" class="animated-button1" onClick={go}>
              <span></span>
              <span></span>
              <span></span>
              <span></span>
              Submit
                </a> */}
                        </section>
                    </center>
                </div>
            )
        } else if (window.location === '/admin') {
            // admin commands page


            if (this.state.track === true) {
                return (
                    <>
                        <label>
                            <input type="text" required value={this.state.usernamet} onChange={(e) => this.setState({ usernamet: e.target.value })} />
                            <div class="label-text">Username of the person you want to track</div>
                        </label>
                        <br />
                        <br />
                        <button onClick={() => this.gousername(this.state.usernamet)}>Usernames</button>
                    </>
                )
            }
            return (
                <div>
                    <title>Admin Commands</title>
                    <label>
                        <input type="text" required value={this.state.cmd} onChange={(e) => { this.setState({ cmd: e.target.value }); this.state.cmd.toUpperCase() }} />
                        <div class="label-text">Command</div>
                    </label>

                    <br />
                    <br />
                    <button type="submit" onClick={() => this.runcmd(this.state.cmd)} >Run</button>
                    <button onClick={go}>Commands</button>
                </div>
            )
        }

    }
}

export default App;
