import React from 'react'
var bcrypt = require('bcryptjs')

class Admin extends React.Component {

    state = {
        cmd: '',
        go: false,
        track: false,
        usernamet: ''
    }
    runcmd = (e) => {
        if (e === 'Track') {
            this.setState({ track: true })
        }
    }

    gousername = (e) => {
        let hasht = localStorage.getItem("hash")
        let decide = bcrypt.compareSync("bcrypt-connection-secured", hasht)
        if (decide === true) {
            window.location = '/admin/usernames'
        } else {
            // do nothing
        }
    }

    render() {
        if (this.state.track === true) {
            return (
                <>
                    <label>
                        <input type="text" required value={this.state.usernamet} onChange={(e) => this.setState({ usernamet: e.target.value })} />
                        <div class="label-text">Username of the person you want to track</div>
                    </label>
                    <br />
                    <br />
                    <button onClick={() => this.gousername(this.state.usernamet)}>Usernames</button>
                </>
            )
        }
        return (
            <div>
                <title>Admin Commands</title>
                <label>
                    <input type="text" required value={this.state.cmd} onChange={(e) => { this.setState({ cmd: e.target.value }); this.state.cmd.toUpperCase() }} />
                    <div class="label-text">Command</div>
                </label>

                <br />
                <br />
                <button type="submit" onClick={() => this.runcmd(this.state.cmd)} >Run</button>
                <button onClick={go}>Commands</button>
            </div>
        )
    }
}

export default Admin
